    <style>
        .payment-btn {
            border: none;
            background: none;
            padding: 0;
            width: 100%;
        }

        .payment-btn:disabled {
            opacity: 0.7;
            cursor: not-allowed;
        }

        .payment-btn:disabled .elementor-button {
            pointer-events: none;
        }
    </style>
